    			args:{
    				'item_code': frm.doc.item_code
    			},
    			async:false,
    			url: "/api/method/insert_barcode",
    			callback: function(r){

    			}
    		})
    		location.reload();
	        })
        }
	frm.set_query("style_number",function(){
        return{
    		filters: {
    		    "disabled":["!=",1],
    		  }
        };
    });
    },
	before_save: function(frm){
	    var brand;
	    var year;
	    var collection;
	    var sub_category;
	    var category;
	    var style;
	    var res;
	    if(frm.doc.variant_of == undefined && frm.doc.is_finished_product == 1 && frm.doc.fabric_or_yarn != 1 && frm.doc.is_other_raw_material != 1){
	        brand = frm.doc.brand_code;
	        year = frm.doc.launch_year;
	        collection = frm.doc.collection_code;
	        style = frm.doc.style_number;
	        frappe.db.get_value("Item Group",{"name":frm.doc.item_group},["sub_category_code","parent_item_group"],(s)=>{
	          sub_category=s.sub_category_code;
	          frappe.db.get_value("Item Group",{"name":s.parent_item_group},"category_code",(c)=>{
	              category=c.category_code
	              if(category == undefined && sub_category != undefined){
	             res = brand.concat(year,collection,sub_category,style)
	              }else if(category != undefined && sub_category == undefined) {
	             res = brand.concat(year,collection,category,style)
	              }else if(category != undefined && sub_category != undefined) {
	             res = brand.concat(year,collection,category,sub_category,style)
	              }else if(category == undefined && sub_category == undefined) {
	             res = brand.concat(year,collection,style)
	              }
	              frm.set_value("item_code",res);
	              frm.refresh_field("item_code");
	          })
	        });
	    }
	    else{}
	    if(frm.doc.variant_of == undefined && frm.doc.is_finished_product != 1 && frm.doc.intermediate_product == 1 && frm.doc.fabric_or_yarn != 1 && frm.doc.is_other_raw_material != 1){
	        brand = frm.doc.brand_code;
	        year = frm.doc.launch_year;
	        collection = frm.doc.collection_code;
	        style = frm.doc.style_number;
	        frappe.db.get_value("Item Group",{"name":frm.doc.item_group},["sub_category_code","parent_item_group"],(s)=>{
	          sub_category=s.sub_category_code;
	          frappe.db.get_value("Item Group",{"name":s.parent_item_group},"category_code",(c)=>{
	              category=c.category_code
	              if(category == undefined && sub_category != undefined){
	             res = brand.concat(year,collection,sub_category,style)
	              }else if(category != undefined && sub_category == undefined) {
	             res = brand.concat(year,collection,category,style)
	              }else if(category != undefined && sub_category != undefined) {
	             res = brand.concat(year,collection,category,sub_category,style)
	              }else if(category == undefined && sub_category == undefined) {
	             res = brand.concat(year,collection,style)
	              }
	              frm.set_value("item_code","I-"+res);
	              frm.refresh_field("item_code");
	          })
	        });
	    }

	    if(frm.doc.variant_of == undefined && frm.doc.fabric_or_yarn == 1 && frm.doc.is_other_raw_material != 1){
	        var material = frm.doc.material;
	        var composition = frm.doc.composition;
	        var type = frm.doc.type;
	        var counts = frm.doc.count;
	        var process = frm.doc.process;
	        var weft_count = frm.doc.weft_count;
	        frappe.db.get_value("Item Group",{"name":frm.doc.item_group},["sub_category_code","parent_item_group"],(s)=>{
	          sub_category=s.sub_category_code;
	          frappe.db.get_value("Item Group",{"name":s.parent_item_group},"category_code",(c)=>{
	              category=c.category_code
	              if(category == undefined && sub_category != undefined){
	             res = sub_category.concat("-",material,"-",type,"-",counts,"-",weft_count,"-",process)
	              }else if(category != undefined && sub_category == undefined) {
	             res = category.concat("-",material,"-",type,"-",counts,"-",weft_count,"-",process)
	              }else if(category != undefined && sub_category != undefined) {
	             res = category.concat("-",sub_category,"-",material,"-",type,"-",counts,"-",weft_count,"-",process)
	              }else if(category == undefined && sub_category == undefined) {
	             res = material.concat("-",type,"-",counts,"-",weft_count,"-",process)
	              }
	              frm.set_value("item_code",res);
	              frm.refresh_field("item_code");
	          })
	        });
	    }

	    if(frm.doc.variant_of == undefined && frm.doc.is_finished_product != 1 && frm.doc.intermediate_product != 1 && frm.doc.fabric_or_yarn != 1 && frm.doc.is_other_raw_material == 1){
	        var i_name = frm.doc.item_name;
	        frappe.db.get_value("Item Group",{"name":frm.doc.item_group},["sub_category_code","parent_item_group"],(s)=>{
	          sub_category=s.sub_category_code;
	          frappe.db.get_value("Item Group",{"name":s.parent_item_group},"category_code",(c)=>{
	              category=c.category_code
	              if(category == undefined && sub_category != undefined){
	             res = sub_category.concat("-",i_name)
	              }else if(category != undefined && sub_category == undefined) {
	             res = category.concat("-",i_name)
	              }else if(category != undefined && sub_category != undefined) {
	             res = category.concat("-",sub_category,"-",i_name)
	              }else if(category == undefined && sub_category == undefined) {
	             res = i_name
	              }
	              frm.set_value("item_code",res);
	              frm.refresh_field("item_code");
	          })
	        });
	    }

	    var count = 0;
	    if(frm.doc.has_variants == 1 && frm.doc.fabric_or_yarn != 1 && frm.doc.is_finished_product == 1 && frm.doc.is_other_raw_material != 1){
	        $.each(frm.doc.attributes, function(idx,item){
	            var name=item.attribute;
	            var str = name.toLowerCase();
	            if(str.includes("size")){
	                count= count +1
	            }
	            if(str.includes("colour") || str.includes("color")){
	                count= count +1
	            }
	        })
	        if(count<2){
	            frappe.validate=false;
	            frappe.throw(__("For variants item, attributes table should have both colour and size attributes."))
	        }
	    }
	    count=0;
	    if(frm.doc.has_variants == 1 && frm.doc.intermediate_product == 1 && frm.doc.fabric_or_yarn != 1 && frm.doc.is_other_raw_material != 1){
	        $.each(frm.doc.attributes, function(idx,item){
	            var name=item.attribute;
	            var str = name.toLowerCase();
	            if(str.includes("size")){
	                count= count +1
	            }
	            if(str.includes("colour") || str.includes("color")){
	                count= count +1
	            }
	            if(str.includes("stage")){
	                count= count +1
	            }
	        })
	        if(count<3){
	            frappe.validate=false;
	            frappe.throw(__("For variants item, attributes table should have colour, size, stage attributes."))
	        }
	    }
	    count=0;
	    var i_group = frm.doc.item_group.toLowerCase();
	    if(frm.doc.has_variants == 1 && i_group.includes("fabric") && frm.doc.fabric_or_yarn == 1 && frm.doc.is_other_raw_material != 1){
	        $.each(frm.doc.attributes, function(idx,item){
	            var name=item.attribute;
	            var str = name.toLowerCase();
	            if(str.includes("width")){
	                count= count +1
	            }
	            if(str.includes("colour") || str.includes("color")){
	                count= count +1
	            }
	        })
	        if(count<2){
	            frappe.validate=false;
	            frappe.throw(__("For variants item, attributes table should have colour and width attributes."))
	        }
	    }
	    count=0;
	    if(frm.doc.has_variants == 1 && i_group.includes("yarn") && frm.doc.fabric_or_yarn == 1 && frm.doc.is_other_raw_material != 1){
	        $.each(frm.doc.attributes, function(idx,item){
	            var name=item.attribute;
	            var str = name.toLowerCase();
	            if(str.includes("form")){
	                count= count +1
	            }
	            if(str.includes("colour") || str.includes("color")){
	                count= count +1
	            }
	        })
	        if(count<2){
	            frappe.validate=false;
	            frappe.throw(__("For variants item, attributes table should have colour and form attributes."))
	        }
	    }
	}
})

