frappe.ui.form.on('Sales Taxes and Charges', {
	rate:function(frm, cdt, cdn) {
		update_net_total(frm,cdt,cdn);
		calculate_tax_rate(frm,cdt,cdn);
	},
	included_in_print_rate:function(frm,cdt,cdn){
		update_net_total(frm,cdt,cdn);
		calculate_tax_rate(frm,cdt,cdn);	    
	},
	taxes_remove:function(frm, cdt, cdn){
	    update_net_total(frm,cdt,cdn);
		calculate_tax_rate(frm,cdt,cdn);
	},
	account_head:function(frm,cdt,cdn){
	    var d = locals[cdt][cdn];
	    let ac_head = d.account_head;
	    if(d.account_head != null){
    	    if(ac_head.includes("%")){
    	        const desc = ac_head.split("%");
    	        d.description = desc[0]+"%" ;
    	    }else{
    	        d.description = ac_head;
    	    }
    	    refresh_field("taxes");
	    }
	},
	taxes_add:function(frm, cdt, cdn){
	    var d = locals[cdt][cdn];
	    d.charge_type = "On Net Total";
	    refresh_field("taxes");
	}
})

function calculate_nettotal_and_total(frm,cdt,cdn){
   var d = locals[cdt][cdn];
   var total = 0;
   frm.doc.items.forEach(function(d) { total += (d.qty*d.basic_rate); });
   frm.set_value("net_total", total);
   refresh_field("net_total");
   frm.set_value("total", total);
   refresh_field("total");
}

function update_net_total(frm,cdt,cdn){
	var d = locals[cdt][cdn];
	var total_rate = 0;
	frm.doc.taxes.forEach(function(d) {
		if(d.included_in_print_rate == 1 && d.rate != undefined){
			total_rate += d.rate; 
		} 
	});
	if(total_rate > 0){
		var total_amt = 0;
		total_amt = (frm.doc.total - ((frm.doc.total* total_rate)/(100+total_rate)));
    	frm.set_value("net_total",total_amt);
    	refresh_field("net_total");
	}else{
		frm.set_value("net_total",frm.doc.total);
		refresh_field("net_total");
	}
}

function calculate_tax_rate(frm,cdt,cdn){
    var d = locals[cdt][cdn];
    var total_tax_amt = frm.doc.net_total;
    $.each(frm.doc.taxes, function(idx, item){
        var tax_amt = 0;
		tax_amt = frm.doc.net_total * (item.rate/100);
		item.tax_amount = tax_amt;
		total_tax_amt += tax_amt;
		item.total = total_tax_amt;
    })
    refresh_field("taxes");
}

