function apply_filter(frm){
	var si = []

    frappe.call({
	type:"POST",
	args:{
			'delivery_note': frm.doc.delivery_note
		},
	async:false,
	url: "/api/method/get_sales_invoice",
	callback: function(r){
            for(var i=0;i<r.message.length;i++){
				si.push(r.message[i].parent);
			}
		}
	})

    frm.set_query("sales_invoice",function(){
		return{
			filters: {
			"name":["in",si]
			}
		};
	});
}

