frappe.ui.form.on('POS Invoice', {
    refresh(frm) {
	    if(frm.doc.__islocal){
    		frappe.db.get_value("Fiscal Year",{"disabled":0,"year_start_date":["<=",frm.doc.posting_date],"year_end_date":[">=",frm.doc.posting_date]},"year",(y)=>{
    		        frm.set_value("financial_year",y.year)
    		})
		}
	},
    validate(frm){
        if(frm.doc.is_return==1 && frm.doc.return_against != undefined){
		    frappe.db.get_value("POS Invoice",{"name":frm.doc.return_against},"transaction_id",(t)=>{

    		        if(frm.doc.transaction_id == t.transaction_id){
    		            frm.set_value("transaction_id",undefined);
    		        }
    		})
		}
		if(frm.doc.amended_from != undefined){
		    frappe.db.get_value("POS Invoice",{"name":frm.doc.amended_from},"transaction_id",(t)=>{

    		        if(frm.doc.transaction_id == t.transaction_id){
    		            frm.set_value("transaction_id",undefined);
    		        }
    		})
		}
        if(frm.doc.transaction_id!=null && frm.doc.transaction_id!=undefined){
	        for(var i=0;i<frm.doc.payments.length;i++){
	            if(frm.doc.payments[i].mode_of_payment=="Online" && frm.doc.payments[i].mode_of_payment!=undefined && frm.doc.payments[i].base_amount>0){
	                frm.doc.payments[i].transaction_id=frm.doc.transaction_id
	            }
	            if(frm.doc.is_return==1){
	                if(frm.doc.payments[i].mode_of_payment=="Online" && frm.doc.payments[i].mode_of_payment!=undefined && frm.doc.payments[i].base_amount<0){
	                    frm.doc.payments[i].transaction_id=frm.doc.transaction_id
	                            if(frm.doc.is_return==1 && frm.doc.return_against != undefined){
                        		    frappe.db.get_value("POS Invoice",{"name":frm.doc.return_against},"transaction_id",(t)=>{

                		        if(frm.doc.payments[i].transaction_id == t.transaction_id){
                		            frm.doc.payments[i].transaction_id = undefined;
                		        }
                		})
            		}
	                   } 
	                }
	        }
	    }
	    if(frm.doc.payment_source!=null && frm.doc.payment_source!=undefined){
	        for(var k=0;k<frm.doc.payments.length;k++){
	            if(frm.doc.payments[k].mode_of_payment=="Online" && frm.doc.payments[k].mode_of_payment!=undefined && frm.doc.payments[k].base_amount>0){
	                frm.doc.payments[k].payment_source=frm.doc.payment_source
	            }
	            if(frm.doc.is_return==1){
	                if(frm.doc.payments[k].mode_of_payment=="Online" && frm.doc.payments[k].mode_of_payment!=undefined && frm.doc.payments[k].base_amount<0 && frm.doc.transaction_id!=undefined){
	                    frm.doc.payments[k].payment_source=frm.doc.payment_source
	                }
	            }
	        }
	    }
		if (frm.doc.payments!=null) {
			for(var j=0;j<frm.doc.payments.length;j++){
	            if(frm.doc.payments[j].mode_of_payment=="Online" && frm.doc.payments[j].mode_of_payment!=undefined && frm.doc.payments[j].base_amount>0){
	                if(frm.doc.payments[j].transaction_id==null||frm.doc.payments[j].transaction_id==undefined||frm.doc.payments[j].transaction_id==''){
	                    frappe.msgprint(__("Unable to submit the invoice due to unavailability of Transaction Id. Please provide the transaction Id to submit the invoice"));
			            frappe.validated = false;
	                }
	            }
	        }
		}
	}
})

