[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<html>
  <html>
    <head>
      <style>
        table, th,td {
        border: 2px solid black;
        border-collapse: collapse;
        background-color: white;
        margin-top: 3px;
        }
      </style>
    <body>
        <p style="text-align: center; font-size: 15px;"><b>Credit Note</b></p>
      <table style="width:100%">
        <tr>
          <td rowspan="5">
            <h3 style="text-align: left; font-size: 15px; ">
              <b>{{ doc.company}}</b>
            </h3>
            <h6 style="text-align: left; font-size: 10px; "><b>	
              {% set val= doc.company_address %}
              {%if doc.company_address != None%}
              {{frappe.db.get_value("Address",val,"address_line1")}}<br>
              {% if frappe.db.get_value("Address",val,"address_line2") == None %}
              {{ " "}}
              {% else %}
              {{frappe.db.get_value("Address",val,"address_line2")}}
              {% endif %}
              {{frappe.db.get_value("Address",val,"city")}}-
              {{frappe.db.get_value("Address",val,"pincode")}}.
              <br>Contact No: 
              {% if frappe.db.get_value("Address",val,"Phone") == None %}
              {{ " "}}
              {% else %}
              +91-{{frappe.db.get_value("Address",val,"Phone")}}
              {% endif %}
              <br>
              E-Mail:
              {% if frappe.db.get_value("Address",val,"email_id") == None %}
              {{ " "}}
              {% else %}
              {{frappe.db.get_value("Address",val,"email_id")}} 
              {% endif %}
              {% if frappe.db.get_value("Company",doc.company,"email") == None %}
              {{""}}
              {% else %}
              & {{frappe.db.get_value("Company",doc.company,"email")}},
              {% endif %}
              Website:
              {% if frappe.db.get_value("Company",doc.company,"website") == None %}
              {{""}}
              {% else %}
              {{frappe.db.get_value("Company",doc.company,"website")}}
              {% endif %}
              </b><br>  <b>GSTIN: 
              {% if frappe.db.get_value("Address",val,"gstin") == None %}
              {{""}}
              {% else %}
              {{frappe.db.get_value("Address",val,"gstin")}} 
              PAN No: {% if frappe.db.get_value("Company",doc.company,"tax_id") == None %}
              {{""}}
              {% else %}
              {{frappe.db.get_value("Company",doc.company,"tax_id")}}
              {% endif %}</b>
              {% endif %}
              {% endif %}	
            </h6>
          </td>
          <td style="font-size: 11px;">
            <b>Invoice No.</b>
            <br>{{doc.name}}
          </td>
          <td style="font-size: 11px;">
            <b>Dated</b>
            <br>{{doc.get_formatted("posting_date")}}
          </td>
        </tr>
        <tr>
          <td style="font-size: 11px;">
            <b>Delivery Note</b>
            <br>{%if doc.delivery_note != None%}{{doc.delivery_note}}{%endif%}
          </td>
          <td style="font-size: 11px;">
            <b>Mode/Terms of Payment</b>
            {%for row in doc.payments%}
            {%if row.mode_of_payment != None%}<br>{{row.mode_of_payment}}{%endif%}
            {%endfor%}
          </td>
        </tr>
        <tr>
            <td style="font-size: 11px;">
            <b>Supplier’s Ref.</b>
            <br> {%if doc.delivery_note != None%}
            {%set so = frappe.db.get_value("Delivery Note Item",{"parent":doc.delivery_note,"parenttype":"Delivery Note"},"against_sales_order")%}
            {%if so !=None%}{{so}}{%endif%}
            {%endif%}
            </td>
            <td style="font-size: 11px;">
            <b>Other Reference(s)</b>
            </td>
        </tr>
        <tr>
            <td style="font-size: 11px;">
            <b>Buyer’s Order No.</b>
            <p>{%if doc.po_no != None%}{{doc.po_no}}{%endif%}</p>
            </td>
            <td style="font-size: 11px;">
            <b>Dated</b>
            <p>{%if doc.po_date != None%}{{doc.get_formatted("po_date")}}{%endif%}</p>
            </td>
        </tr>
        <tr>
            <td style="font-size: 11px;">
            <b>Despatch Document No</b><br>{%if doc.dispatch_document_no != None%}{{doc.dispatch_document_no}}{%endif%}
            </td>
            <td style="font-size: 11px;">
            <b>Delivery Note Date</b>
            {%if doc.delivery_note != None%}
            {% set dnd =frappe.db.get_value("Delivery Note",{"name":doc.delivery_note},"posting_date")%}
            {%if dnd!=None%}<p>{{dnd.strftime('%d-%m-%Y')}}</p>{%endif%} {%endif%}
            </td>
        </tr>
        <tr>
            <td rowspan="2" style="font-size: 14px;">
                <b>Buyer</b><br>
               <p style="font-size: 14px;"> <b>{{doc.customer}}</b></p><br>
                {%if doc.address_display !=None%}
               <p style="font-size:10px"><b> {{doc.address_display}}</b></p>
                {%endif%}
            </td>
            <td rowspan="1" style="line-height: 10px; font-size: 11px;">
                <b>Despatched through</b>
                <p>{%if doc.mode_of_transport != None%}{{doc.mode_of_transport}}{%endif%}</p>
            </td>
            <td rowspan="1" style="font-size: 11px;">
                <b>Destination</b>
                <br>{%if doc.destination != None%}{{doc.destination}}{%endif%}
            </td>
        </tr>
        <tr>
            <td colspan="2" rowspan="1" style="height: auto; font-size: 11px;"> 
                <b>Terms of Delivery</b><br>
                {#%if doc.delivery_note!=None%}
                {%set term = frappe.db.get_value("Delivery Note",{"name":doc.delivery_note},"terms")%#}
                {%if doc.terms!= None%}
                {{doc.terms}} {%endif%}
                {%if term==None%}
                <br>&nbsp;
                <br>&nbsp;
                <br>&nbsp;
                <br>&nbsp;
                {%endif%}
                {#%endif%#}
            </td>
        </tr>
      </table>
    </body>
  </html>
"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<html>
<head>
<style>
table, th,td {
  border: 2px solid black;
  border-collapse: collapse;
  background-color: white;
  margin-top: -1px;
}
</style>
</head>
    <body>
    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <thead style="background-color:white">
            <tr>
                <th style="text-align:center;font-size:9px;"><b>Sl.No</b></th>
                <th style="text-align:center;font-size:9px;"><b>Description of Goods</b> </th>
                <th style="text-align:center;font-size:9px;"><b>HSN code</b></th>
                <th style="text-align:center;font-size:9px;"><b>GST Rate</b></th>
                <th style="text-align:center;font-size:9px;"><b>Quantity</b></th>
                <th style="text-align:center;font-size:9px;"><b>Rate</b></th>
                <th style="text-align:center;font-size:9px;"><b>per</b></th>
                <th style="text-align:center;font-size:9px;"><b>Disc. %</b></th>
                <th style="text-align:center;font-size:9px;"><b>Amount</b></th>
            </tr>
        </thead>
        <tbody style="font-size:10px;">
        {% for item in doc.items %}
                <tr>
                    <td>{{ item.idx }}</td>
                    <td> <b>{{ item.item_code}}</b><br>
                    {% if item.item_code != item.item_name -%}
                    {%if item.item_name != None%}
                        {{ item.item_name}}
                        {%endif%}
                        {%- endif %}
                    </td>
                    <td style="text-align:left">
                    {% if item.gst_hsn_code == None %}
                    {{" "}}
                    {% else %}
                    {{ item.gst_hsn_code}}
                    {% endif %}
                    </td>
                    <td style="text-align:right">
		{%set tax = [0] %}
                        {% for row in doc.taxes %}
            {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
            {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                {%if item_code==item.item_code%}
                {% if row.rate== 0 %}

                {% if tax_amounts[0] > 0 %}
		{% set _ =tax.append( tax.pop() + tax_amounts[0]) %}

		{%endif%}
                {%endif%}{%endif%}
                {% endfor %}
            {% endfor %} {{tax[0]}} % 
                    </td>
                    <td style="text-align:right;">{{item.qty | abs}} {{item.uom}}</td>
                    <td style="text-align:right;">{{item.rate}}</td>
                    <td>{{item.uom}}</td>
                    <td style="text-align:right;">{%if item.discount_percentage != None%}{{item.discount_percentage}} %{%endif%}</td>
                    <td style="text-align:right;">{{item.amount | abs}}</td>
                </tr>
            {% endfor %} 
            <tr>
                <td></td>
                <td style="text-align: right;"><b>Total</b></td>
                <td></td>
                <td></td>
                <td style="text-align: right;">{{doc.total_qty | abs}} {{frappe.db.get_value("Sales Invoice Item",{"parent":doc.name,"parenttype":"Sales Invoice"},"uom")}}</td>
                <td></td>
                <td></td>
                <td></td>
                <td style="text-align: right;">{{frappe.db.get_value("Currency",{"name":doc.currency},"symbol")}} {{doc.rounded_total|abs}}</td>
            </tr>
            <tr>
                <td colspan="9">
                    Amount Chargeable (in Words): 
                    <b>{{doc.in_words}}</b>
                </td>
            </tr>
        </tbody>
    </table>
    <table style="line-height: 1em;width: 100%; font-size: 10px" border="2" cellpadding="0" cellspacing="0">
        <thead style="background-color:white;">
            <tr>
                <th rowspan="2" style="text-align: center;">HSN/SAC</th>
                <th rowspan="2" style="text-align: center;">Taxable Value</th>
                <th colspan="2" style="text-align: center;">Central Tax</th>
                <th colspan="2" style="text-align: center;">State Tax</th>
                <th colspan="2" style="text-align: center;">IGST</th>
                <th rowspan="2" style="text-align: center;">Total Tax Amount</th>
            </tr>
            <tr>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
            </tr>
        </thead>
        <tbody style="font-size:10px;">
            <tr>
                <td>
                {%set hsn = [""] %}
                {%for item in doc.items %}
                {%if item.gst_hsn_code != None%}
                {% if item.gst_hsn_code not in hsn %}
                {%set _ = hsn.append(item.gst_hsn_code)%}
                {%set _ = hsn.append("<br>")%}
                {%endif%}
                {%endif%}
                {%endfor%}
                {% for i in hsn %}{{i}}
                {%endfor%}
		</td>
                <td style="text-align: right;">{{doc.net_total | abs}}</td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'CGST' in row.account_head%}
                            {%if row.rate > 0%} {{row.rate}} {%else%}
                             {% set ctax = frappe.db.get_value("Item Tax Template Detail",{"tax_type":row.account_head},"tax_rate")%}
                             {%if ctax != None %} {{ctax}}{% else %} {% set desc = row.description.split('-')%}{{desc[1]}}{%endif%}
                            {%endif%}<br>
                            {%endif%}
                    {% endfor %}
                </td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'CGST' in row.account_head%}
                            {{row.tax_amount | abs}}<br>
                        {%endif%}
                    {% endfor %}
                </td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'SGST' in row.account_head%}
                            {%if row.rate > 0%} {{row.rate}} {%else%}
                             {% set stax = frappe.db.get_value("Item Tax Template Detail",{"tax_type":row.account_head},"tax_rate")%}
                             {%if stax != None %} {{stax}}{% else %} {% set desc = row.description.split('-')%}{{desc[1]}} {%endif%}
                            {%endif%}<br>
                            {%endif%}
                    {% endfor %}</td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'SGST' in row.account_head%}
                            {{row.tax_amount|abs}}<br>
                        {%endif%}
                    {% endfor %}
                </td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'IGST' in row.account_head%}
                            {%if row.rate > 0%} {{row.rate}} {%else%}
                             {% set itax = frappe.db.get_value("Item Tax Template Detail",{"tax_type":row.account_head},"tax_rate")%}
                             {%if itax != None %} {{itax}}{% else %} {% set desc = row.description.split('-')%}{{desc[1]}} {%endif%}
                            {%endif%}<br>
                        {%endif%}
                    {% endfor %}</td>
                <td style="text-align: right;">
                    {% for row in doc.taxes %}
                        {%if 'IGST' in row.account_head%}
                            {{row.tax_amount | abs}}<br>
                        {%endif%}
                    {% endfor %}
                </td>
                <td style="text-align: right;">{% if doc.total_taxes_and_charges != None %}{{doc.total_taxes_and_charges | abs}}{%endif%}</td>
            </tr>
            <tr>
                <td colspan="9">Tax Amount(in words) : <b>{{ frappe.utils.money_in_words(doc.total_taxes_and_charges | abs) }}</b></td>
            </tr>
        </tbody>
    </table>
    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <tr>
        <td width="50%">
            <p><u>Declaration</u></p>
            <p>We declare that this invoice shows the actual price of the
goods described and that all particulars are true.</p>
        </td>
        <td width="50%">
            <p><b>Company’s Bank Details</b></p>
            <b>Bank Name         :</b>{%set b_name=frappe.db.get_value("Bank Account",{"is_default":1},"bank")%}{%if b_name != None%}{{b_name}}{%endif%}<br>
            <b>A/c No.           :</b>{%set b_ac=frappe.db.get_value("Bank Account",{"is_default":1},"bank_account_no")%}{%if b_ac != None%}{{b_ac}}{%endif%}<br>
            <b>Branch & IFS Code :</b>{%set b_code=frappe.db.get_value("Bank Account",{"is_default":1},"branch_code")%}{%if b_code != None%}{{b_code}}{%endif%}<br>
        </td>
        </tr>
    </table>
    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <tr>
        <td width="50%"><p>Customer’s Seal and Signature </p><br>&nbsp;<br>&nbsp;<br>&nbsp;</td>
        <td width="50%" style="text-align: right;">
            {%set year= frappe.db.get_value("Fiscal Year",{"disabled":0,"year_start_date":["<=",doc.posting_date],"year_end_date":[">=",doc.posting_date]},"name")%}
            <b>for Rangsutra Crafts India Ltd-{{year}}</b><br><br>&nbsp;<br>&nbsp;<br>Authorised Signatory</td>
        </tr>
    </table>
    <p style="text-align: center;">This is a computer generated invoice.</p>
    </body>
</html>"}]

