    <table width=100% style="font-size: 11px;">
        <tr>
            <td width=40%>{%if doc.remarks != None and doc.remarks != "No Remarks"%}{{doc.remarks}}{%endif%}</td>
            <td>
                {%- for row in doc.taxes -%}
                    {%if row.tax_amount != 0%}
                    {% if '%' in row.description %}
					    {{ row.description }}
					{% else %}
					    {{ row.description }}@{{ row.rate }}%
					{% endif %}
					&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;&emsp;{{symbol}} {{row.tax_amount|abs}}<br>
                    {%endif%}
                {%endfor%}
            </td>
        </tr>
