                <td style="text-align: right;">
                 {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'SGST' in tax%}
                {%if '%' in tax %}{% set desc = tax.split('-')%}{{desc[1]}}{%endif%} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'SGST' in tax%}
                    {{ items|sum(attribute="tax_amount")|round(2, 'ceil') }} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                 {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'IGST' in tax%}
                {%if '%' in tax %}{% set desc = tax.split('-')%}{{desc[1]}}{%endif%} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'IGST' in tax%}
                    {{ items|sum(attribute="tax_amount")|round(2, 'ceil') }} <br>
                 {%endif%}
                {%endfor %}
