		<td style="text-align: left;"><b>Total Amount</b></td>
		<td>{%if doc.rounded_total != 0 %}{{doc.rounded_total}}{%else%}{{doc.grand_total}}{%endif%}</td>
	    </tr>
            <tr>
                <td colspan="11">Tax Amount(in words) : <b>{{ frappe.utils.money_in_words(doc.total_taxes_and_charges) }}</b></td>
            </tr>
        </tbody>
    </table>
    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <tr>
        <td width="50%">
            <p><u>Declaration</u></p>
            <p>We declare that this invoice shows the actual price of the
goods described and that all particulars are true.</p>
        </td>
        <td width="50%">
            <p><b>Company’s Bank Details</b></p>
       {%set AD = frappe.db.get_value("Accounting Dimension",{"document_type":"Branch"},"document_type")%}
	   {%if doc.branch != None and AD == "Branch"%}
            <b>Bank Name         :</b>{%set b_name=frappe.db.get_value("Bank Account",{"is_default":1,"branch":doc.branch},"bank")%}{%if b_name != None%}{{b_name}}{%endif%}<br>
            <b>A/c No.           :</b>{%set b_ac=frappe.db.get_value("Bank Account",{"is_default":1,"branch":doc.branch},"bank_account_no")%}{%if b_ac != None%}{{b_ac}}{%endif%}<br>
            <b>Branch & IFS Code :</b>{%set b_code=frappe.db.get_value("Bank Account",{"is_default":1,"branch":doc.branch},"branch_code")%}{%if b_code != None%}{{b_code}}{%endif%}<br>
	   {%else%}
	   <b>Bank Name         :</b>{%set b_name=frappe.db.get_value("Bank Account",{"is_default":1},"bank")%}{%if b_name != None%}{{b_name}}{%endif%}<br>
            <b>A/c No.           :</b>{%set b_ac=frappe.db.get_value("Bank Account",{"is_default":1},"bank_account_no")%}{%if b_ac != None%}{{b_ac}}{%endif%}<br>
            <b>Branch & IFS Code :</b>{%set b_code=frappe.db.get_value("Bank Account",{"is_default":1},"branch_code")%}{%if b_code != None%}{{b_code}}{%endif%}<br>
	   {%endif%}
        </td>
        </tr>
    </table>
    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <tr>
        <td width="50%"><p>Customer’s Seal and Signature </p><br>&nbsp;<br>&nbsp;<br>&nbsp;</td>
        <td width="50%" style="text-align: right;">
            {%set year= frappe.db.get_value("Fiscal Year",{"disabled":0,"year_start_date":["<=",doc.posting_date],"year_end_date":[">=",doc.posting_date]},"name")%}
            <b>for Rangsutra Crafts India Ltd-{{year}}</b><br><br>&nbsp;<br>&nbsp;<br>Authorised Signatory</td>
        </tr>
    </table>
    <p style="text-align: center;">This is a computer generated invoice.</p>
    </body>
</html>"}]

[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<html></html>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html>
<head>
    <style>
        .print-format table,
        .print-format tr,
        .print-format td,
        .print-format div{
            color: black;
