    </style>
</head>
<body>

    <table style="width: 100%;">
        {%- for i in range(0, doc.print_label_quantity| int, 1) -%}
                <tr>
                    <td style="width: 100%; text-align: center;">
                        <div style="text-align: center;color: black;height: 2.54cm;line-height: 120%;">
                            <p style="font-size: 16px;">
                                 {% if doc.print_name != None %}
                                <b style=" display:inline-block; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; max-width: 30ch;">{{ doc.print_name }}</b>
                                {% else %}
                                <br>
                                {% endif %}
                            </p>
                            <p style="font-size:14px;">
                                {% set rate =frappe.db.get_value("Item Price",{"item_code":doc.item_code},"price_list_rate") %}
                                {% set currency =frappe.db.get_value("Item Price",{"item_code":doc.item_code},"currency") %}
                                {% set symbol =frappe.db.get_value("Currency",{"name":currency},"symbol") %}
                                {% set size =frappe.db.get_value("Item Variant Attribute",{"parent":doc.item_code,"parenttype":"Item","attribute":['like', '%size%']},"attribute_value") %} 
                                {% set colour =frappe.db.get_value("Item Variant Attribute",{"parent":doc.item_code,"parenttype":"Item","attribute":['like','%colour%']},"attribute_value") %}
                                {% set colour1 =frappe.db.get_value("Item Variant Attribute",{"parent":doc.item_code,"parenttype":"Item","attribute":['like','%color%']},"attribute_value") %}
                                <b>{%if rate != None %} {{symbol}} {{ rate }}{%endif%} {%if colour != None%}/ {{colour}}{%elif colour1 !=None%}{{colour1}}{%endif%} {%if size != None%}/ {{size}}{%endif%}</b>
                            </p>
                           <svg class="barcode" jsbarcode-margin="1" jsbarcode-margintop="0" jsbarcode-marginbottom="0"
                            jsbarcode-height="30" jsbarcode-width="1" jsbarcode-fontsize="15" jsbarcode-flat="true"
                            jsbarcode-value="{{ doc.item_code }}"></svg>
                        </div>
                    </td>
                </tr>
        {%endfor%}
    </table>
    <script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.11.4/dist/barcodes/JsBarcode.code39.min.js"></script>
    <script>
        JsBarcode(".barcode").init();
    </script>
</body>
</html>"}]

[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<html></html>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
