            {%set sales = frappe.db.get_value("Item",{"name":row.item_code},"is_sales_item")%}
            {%if sales == 1%}
            {%- for i in range(0, row.qty| int, 1) -%}
                <tr>
                    <td style="width: 100%; text-align: center;">
                        <div style="text-align: center;color: black;height: 3.81cm;line-height: 120%;">
                            <p style="font-size: 16px;">
                                {% set print_name = frappe.db.get_value("Item",{"name":row.item_code},"print_name")%}
                                 {% if print_name != None %}
                                <b style=" display:inline-block; white-space: nowrap; overflow: hidden; text-overflow: ellipsis; max-width: 30ch;">{{ print_name }}</b>
                                {% endif %}
                            </p>
                            <p style="font-size:14px;">
                                {% set size =frappe.db.get_value("Item Variant Attribute",{"parent":row.item_code,"parenttype":"Item","attribute":["like","%size%"]},"attribute_value") %} 
                                {% set colour =frappe.db.get_value("Item Variant Attribute",{"parent":row.item_code,"parenttype":"Item","attribute":["like","%colour%"]},"attribute_value") %} 
                                {% set colour1 =frappe.db.get_value("Item Variant Attribute",{"parent":row.item_code,"parenttype":"Item","attribute":["like","%color%"]},"attribute_value") %} 
                                <b>{{ row.get_formatted("basic_rate", doc) }} {%if colour != None%}/ {{colour}}{%elif colour1 !=None%}{{colour1}}{%endif%} {%if size != None%}/ {{size}}{%endif%}</b>
                            </p>
                            <svg class="barcode" jsbarcode-margin="0" jsbarcode-margintop="0" jsbarcode-marginbottom="0"
                            jsbarcode-height="30" jsbarcode-width="1" jsbarcode-fontsize="11" jsbarcode-flat="true"
                            jsbarcode-value="{{ row.item_code }}"></svg>
                        </div>
                    </td>
                </tr>
                {%- endfor -%}
            {%endif%}
        {%- endfor -%}

    </table>
    <script src="https://cdn.jsdelivr.net/npm/jsbarcode@3.11.4/dist/barcodes/JsBarcode.code39.min.js"></script>
    <script>
        JsBarcode(".barcode").init();
    </script>
    {%endif%}
</body>
</html>
"}]

