                {%if doc.terms!= None%}
                {{doc.terms}} {%endif%}
                {%if doc.terms==None%}
                <br>&nbsp;
                <br>&nbsp;
                <br>&nbsp;
                <br>&nbsp;
                {%endif%}
                {#%endif%#}
            </td>
        </tr>
