    <table style="line-height: 1em;width: 100%;" border="2" cellpadding="0" cellspacing="0">
        <thead style="background-color:white">
            <tr>
                <th style="text-align:center;font-size:9px;"><b>Sl.No</b></th>
                <th style="text-align:center;font-size:9px;"><b>Description of Goods</b> </th>
                <th style="text-align:center;font-size:9px;"><b>HSN code</b></th>
                <th style="text-align:center;font-size:9px;"><b>Quantity</b></th>
                <th style="text-align:center;font-size:9px;"><b>Rate</b></th>
                <th style="text-align:center;font-size:9px;"><b>GST Rate</b></th>
                <th style="text-align:center;font-size:9px;"><b>GST Amount</b></th>
                <th style="text-align:center;font-size:9px;"><b>Amount</b></th>
            </tr>
        </thead>
        <tbody style="font-size:10px;">
        {% for item in doc.items %}
                <tr>
                    <td>{{ item.idx }}</td>
                    <td> {{ item.description}}
                    </td>
                    <td style="text-align:left">
                    {% if item.gst_hsn_code == None %}
                    {{" "}}
                    {% else %}
                    {{ item.gst_hsn_code}}
                    {% endif %}
                    </td>
                    <td style="text-align:right;">{{item.qty}}</td>
                    <td style="text-align:right;">{{item.rate}}</td>
                    <td style="text-align:right">
		{#%if item.item_tax_template != None%#}
		{%set tax = [0] %}
                        {% for row in doc.taxes %}
            {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
            {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                {%if item_code==item.item_code%}
                {% if row.rate== 0 %}

                {% if tax_amounts[0] > 0 %}
		{% set _ =tax.append( tax.pop() + tax_amounts[0]) %}

		{%endif%} {% else %}
        {% set _ =tax.append( tax.pop() + row.rate) %}  {#{row.rate}#}
                {%endif%}{%endif%}
                {% endfor %}
            {% endfor %} {{tax[0]}} % {#%endif%#}
                    </td>

                    <td style="text-align:right">
		{#%if item.item_tax_template != None%#}
		{%set taxrate = [0] %}
                        {% for row in doc.taxes %}
            {%set item_wise_tax_detail = json.loads(row.item_wise_tax_detail)%}
            {% for item_code, tax_amounts in item_wise_tax_detail.items()%}

                {%if item_code==item.item_code%}
                {#% if row.rate== 0 %#}

                {#% if tax_amounts[0] > 0 %#}
		{% set _ =taxrate.append( taxrate.pop() + tax_amounts[1]) %}

		{%endif%}
                {% endfor %}
            {% endfor %} {{taxrate[0]|round(2, 'ceil')}} {#%endif%#}
                    </td>

                    <td style="text-align:right;">{{item.amount}}</td>
                </tr>
            {% endfor %} 
            <tr>
                <td></td>
                <td style="text-align: right;"></td>
                <td></td>
                <td style="text-align: right;">{{doc.total_qty}}</td>
                <td></td>
        		<td></td>
        		<td></td>
                <td style="text-align: right;"></td>
            </tr>

        </tbody>
    </table>
    <table style="line-height: 1em;width: 100%; font-size: 10px" border="2" cellpadding="0" cellspacing="0">
        <thead style="background-color:white;">
	    <tr>
		<td colspan="9"></td>
		<td style="text-align: left;"><b>Total Taxable Value</b></td>
		<td>{{doc.net_total}}</td>
	    </tr>
            <tr>
                <th rowspan="2" style="text-align: center;">HSN/SAC</th>
                <th rowspan="2" style="text-align: center;">Taxable Value</th>
                <th colspan="2" style="text-align: center;">Central Tax</th>
                <th colspan="2" style="text-align: center;">State Tax</th>
                <th colspan="2" style="text-align: center;">IGST</th>
                <th rowspan="2" style="text-align: center;">Total Tax Amount</th>
		<td style="text-align: left;"><b>Total Tax Amount</b></td>
		<td>{{doc.total_taxes_and_charges}}</td>
            </tr>
            <tr>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
                <th style="text-align: center;">Rate</th>
                <th style="text-align: center;">Amount</th>
		<td style="text-align: left;"><b>TCS</b></td>
		<td>
		{% for row in doc.taxes %}
		{%if 'TCS' in row.account_head%}
		{{row.tax_amount}}
		{%endif%}
		{%endfor%}
		</td>
            </tr>
        </thead>
        <tbody style="font-size:10px;">
            <tr>
                <td>
                {%set hsn = [""] %}
                {%for item in doc.items %}
                {%if item.gst_hsn_code != None%}
                {% if item.gst_hsn_code not in hsn %}
                {%set _ = hsn.append(item.gst_hsn_code)%}
                {%set _ = hsn.append("<br>")%}
                {%endif%}
                {%endif%}
                {%endfor%}
                {% for i in hsn %}{{i}}
                {%endfor%}
		</td>
                <td style="text-align: right;">{{doc.net_total}}</td>
                <td style="text-align: right;">
                 {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'CGST' in tax%}
                {%if '%' in tax %}{% set desc = tax.split('-')%}{{desc[1]}}{%endif%} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'CGST' in tax%}
                    {{ items|sum(attribute="tax_amount")|round(2, 'ceil') }} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                 {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'SGST' in tax%}
                {%if '%' in tax %}{% set desc = tax.split('-')%}{{desc[1]}}{%endif%} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'SGST' in tax%}
                    {{ items|sum(attribute="tax_amount")|round(2, 'ceil') }} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                 {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'IGST' in tax%}
                {%if '%' in tax %}{% set desc = tax.split('-')%}{{desc[1]}}{%endif%} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">
                {% for tax,items in doc.taxes|groupby("description") %}
                {%if 'IGST' in tax%}
                    {{ items|sum(attribute="tax_amount")|round(2, 'ceil') }} <br>
                 {%endif%}
                {%endfor %}
                </td>
                <td style="text-align: right;">{% if doc.total_taxes_and_charges != None %}{{doc.total_taxes_and_charges}}{%endif%}</td>
		<td style="text-align: left;"><b>Round Off</b></td>
		<td>{{doc.rounding_adjustment}}</td>
            </tr>
	    <tr>
		<td colspan="9"></td>

		<td style="text-align: left;"><b>Total Amount</b></td>
		<td>{%if doc.rounded_total != 0 %}{{doc.rounded_total}}{%else%}{{doc.grand_total}}{%endif%}</td>
	    </tr>
            <tr>
                <td colspan="11">Tax Amount(in words) : <b>{{ frappe.utils.money_in_words(doc.total_taxes_and_charges) }}</b></td>
            </tr>
        </tbody>
    </table>
